import { useState } from "react";

import { Component as Chart } from "react-d3-library";

import node from "./d3data";

import "./App.css";

const cols = new Array(10).fill(1);
const rows = new Array(50)
  .fill(1)
  .map(() => cols.map(() => "a".repeat(Math.floor(Math.random() * 50))));

function App() {
  const [selectedRow, setSelectedRow] = useState();

  return (
    <main>
      <section id="mosaic">
        {cols.map((col, index) => (
          <div key={`chart${index}`}>
            {index === 0 && <Chart data={node} />}
          </div>
        ))}
      </section>
      <section id="main">
        Selected row: {selectedRow}
        <Chart data={node} />
      </section>
      <section>
        <table>
          <thead>
            <tr>
              {cols.map((col, index) => (
                <th key={`feat${index}`}>Feature {index}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {rows.map((row, index) => (
              <tr
                key={`row${index}`}
                className={index === selectedRow ? "selected" : ""}
                onClick={() => setSelectedRow(index)}
              >
                {row.map((col, index) => (
                  <td key={`cell${index}`}>{col}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </main>
  );
}

export default App;
