import { useState } from "react";

import { Component as Chart } from "react-d3-library";

import node from "d3file";

import "./App.css";

const cols = new Array(10).fill(1);
const rows = new Array(50)
  .fill(1)
  .map(() => cols.map(() => "a".repeat(Math.floor(Math.random() * 50))));

function App() {
  const [selectedRow, setSelectedRow] = useState();

  return (
    <main>
      <section id="mosaic">
        {cols.map(() => (
          <div>
            <Chart data={node} />
          </div>
        ))}
      </section>
      <section id="main">Selected row: {selectedRow}</section>
      <section>
        <table>
          <thead>
            {cols.map((col, index) => (
              <th>Feature {index}</th>
            ))}
          </thead>
          <tbody>
            {rows.map((row, index) => (
              <tr
                className={index === selectedRow ? "selected" : ""}
                onClick={() => setSelectedRow(index)}
              >
                {row.map((col) => (
                  <td>{col}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </main>
  );
}

export default App;
