import { useState } from "react";
import { Treemap } from "react-vis";

import "react-vis/dist/style.css";

import "./App.css";

const cols = new Array(10).fill(1);
const rows = new Array(50)
  .fill(1)
  .map(() => cols.map(() => "a".repeat(Math.floor(Math.random() * 50))));

const data = {
  children: [
    { title: "a", color: "red", size: 1 },
    { title: "b", color: "blue", size: 13 },
    { title: "c", color: "green", size: 15 },
    { title: "d", color: "yellow", size: 156 },
  ],
};

function App() {
  const [selectedRow, setSelectedRow] = useState();

  return (
    <main>
      <section id="mosaic">
        {cols.map((col, index) => (
          <div key={`charta${index}`}>a</div>
        ))}
      </section>
      <section id="main">
        Selected row: {selectedRow}
        <Treemap
          title={"Test"}
          width={500}
          height={500}
          data={data}
          mode="binary"
          hideRootNode
          colorType="literal"
          margin={10}
          padding={10}
          onLeafMouseOver={console.log}
          onLeafMouseOut={console.log}
          onLeafClick={console.log}
        />
      </section>
      <section>
        <table>
          <thead>
            <tr>
              {cols.map((col, index) => (
                <th key={`feat${index}`}>Feature {index}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {rows.map((row, index) => (
              <tr
                key={`row${index}`}
                className={index === selectedRow ? "selected" : ""}
                onClick={() => setSelectedRow(index)}
              >
                {row.map((col, index) => (
                  <td key={`cell${index}`}>{col}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </main>
  );
}

export default App;
