import React, { useCallback, useMemo, useRef, useState } from "react";
import { Hint, Treemap } from "react-vis";

import "react-vis/dist/style.css";

import "./App.css";

const features = [
  "LotArea",
  "YearBuilt",
  "GrLivArea",
  "KitchenAbvGr",
  "FirstFlrSF",
  "PoolArea",
  "TotalBsmtSF",
  "BsmtFinSFOne",
  "BsmtUnfSF",
  "GarageYrBlt",
  "EnclosedPorch",
  "WoodDeckSF",
  "MSSubClass",
  "MiscVal",
  "OpenPorchSF",
  "ThreeSsnPorch",
  "YearRemodAdd",
  "YrSold",
  "GarageArea",
  "LowQualFinSF",
  "BsmtFinSFTwo",
  "OverallCond",
  "MoSold",
  "SecondFlrSF",
  "TotRmsAbvGrd",
  "ScreenPorch",
  "Fireplaces",
  "OverallQual",
  "HalfBath",
  "MasVnrArea",
  "GarageCars",
  "LotFrontage",
  "BsmtFullBath",
  "FullBath",
  "BedroomAbvGr",
  "BsmtHalfBath",
];

const tableColumns = ["ID", "Actual", "Predicted", "Difference", ...features];

const tableRows = new Array(tableColumns.length)
  .fill(1)
  .map(() =>
    new Array(tableColumns.length).fill(1).map(() => Math.random() * 1000 - 500)
  );
function App() {
  const [selectedRow, setSelectedRow] = useState();

  const treeChartData = useMemo(
    () => ({
      children:
        selectedRow === undefined
          ? []
          : tableRows[selectedRow].slice(4).map((column, index) => ({
              title: features[index + 4],
              size: Math.abs(column),
              color: column < 0 ? "#bf212f" : "#006f3c",
            })),
    }),
    [selectedRow]
  );

  const treeMapContainer = useCallback((treeMapContainerElement) => {
    if (treeMapContainerElement !== null) {
      console.log(treeMapContainerElement);
    }
  }, []);

  return (
    <main>
      <section id="mosaic">
        {features.map((feature, index) => (
          <div key={`chart-${index}`}>
            {feature} {index}
          </div>
        ))}
      </section>
      <section id="main">
        <Treemap
          title={"Test"}
          width={500}
          height={500}
          data={treeChartData}
          mode="circlePack"
          hideRootNode
          colorType="literal"
          onLeafMouseOver={console.log}
          onLeafMouseOut={console.log}
          onLeafClick={console.log}
        >
          {/*<Hint value={13}>
            <div style={{ background: "black" }}>
              <h3>Value of hint</h3>
        </div>
          </Hint>*/}
        </Treemap>
      </section>
      <section>
        <table>
          <thead>
            <tr>
              {tableColumns.map((column, index) => (
                <th key={`feature-${index}`}>{column}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {tableRows.map((row, index) => (
              <tr
                key={`row-${index}`}
                className={index === selectedRow ? "selected" : ""}
                onClick={() => setSelectedRow(index)}
              >
                {row.map((col, index) => (
                  <td key={`cell-${index}`}>{col}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </main>
  );
}

export default App;
