import { useState } from "react";
import { Treemap } from "react-vis";

//import "./App.css";

const cols = new Array(10).fill(1);
const rows = new Array(50)
  .fill(1)
  .map(() => cols.map(() => "a".repeat(Math.floor(Math.random() * 50))));

const data = {
  title: "analytics",
  color: "#12939A",
  children: [
    {
      title: "cluster",
      children: [
        { title: "AgglomerativeCluster", color: "#00AAFF", size: 3938 },
        { title: "CommunityStructure", color: "#12939A", size: 3812 },
        { title: "HierarchicalCluster", color: "#12939A", size: 6714 },
        { title: "MergeEdge", color: "#12939A", size: 743 },
      ],
    },
    {
      title: "graph",
      children: [
        { title: "BetweennessCentrality", color: "#12939A", size: 3534 },
        { title: "LinkDistance", color: "#12939A", size: 5731 },
        { title: "MaxFlowMinCut", color: "#12939A", size: 7840 },
        { title: "ShortestPaths", color: "#12939A", size: 5914 },
        { title: "SpanningTree", color: "#12939A", size: 3416 },
      ],
    },
    {
      title: "optimization",
      children: [{ title: "AspectRatioBanker", color: "#12939A", size: 7074 }],
    },
  ],
};

function App() {
  const [selectedRow, setSelectedRow] = useState();

  return (
    <main>
      <section id="mosaic">
        {cols.map((col, index) => (
          <div key={`charta${index}`}>a</div>
        ))}
      </section>
      <section id="main">
        Selected row: {selectedRow}
        <Treemap
          title={"Test"}
          width={300}
          height={300}
          data={data}
          mode="binary"
          hideRootNode
        />
      </section>
      <section>
        <table>
          <thead>
            <tr>
              {cols.map((col, index) => (
                <th key={`feat${index}`}>Feature {index}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {rows.map((row, index) => (
              <tr
                key={`row${index}`}
                className={index === selectedRow ? "selected" : ""}
                onClick={() => setSelectedRow(index)}
              >
                {row.map((col, index) => (
                  <td key={`cell${index}`}>{col}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </main>
  );
}

export default App;
