import React, { useCallback, useMemo, useState } from "react";
import { Treemap } from "react-vis";

import "react-vis/dist/style.css";

import "./App.css";

const features = [
  "LotArea",
  "YearBuilt",
  "GrLivArea",
  "KitchenAbvGr",
  "FirstFlrSF",
  "PoolArea",
  "TotalBsmtSF",
  "BsmtFinSFOne",
  "BsmtUnfSF",
  "GarageYrBlt",
  "EnclosedPorch",
  "WoodDeckSF",
  "MSSubClass",
  "MiscVal",
  "OpenPorchSF",
  "ThreeSsnPorch",
  "YearRemodAdd",
  "YrSold",
  "GarageArea",
  "LowQualFinSF",
  "BsmtFinSFTwo",
  "OverallCond",
  "MoSold",
  "SecondFlrSF",
  "TotRmsAbvGrd",
  "ScreenPorch",
  "Fireplaces",
  "OverallQual",
  "HalfBath",
  "MasVnrArea",
  "GarageCars",
  "LotFrontage",
  "BsmtFullBath",
  "FullBath",
  "BedroomAbvGr",
  "BsmtHalfBath",
];

const tableColumns = ["ID", "Actual", "Predicted", "Difference", ...features];

const tableRows = new Array(tableColumns.length).fill(1).map(() => {
  const row = {};
  tableColumns.forEach(
    (tableColumn) => (row[tableColumn] = Math.random() * 1000 - 500)
  );
  return row;
});
function App() {
  const [selectedRow, setSelectedRow] = useState();

  const treeChartData = useMemo(() => {
    const positiveChildren = [];
    const negativeChildren = [];

    if (selectedRow !== undefined) {
      Object.keys(selectedRow).forEach((feature) => {
        console.log(feature);
        let children;
        const value = selectedRow[feature];
        if (value < 0) {
          children = negativeChildren;
        } else {
          children = positiveChildren;
        }
        children.push({ title: feature, size: Math.abs(value) });
      });
    }

    /*const children =
      selectedRow === undefined
        ? []
        : tableRows[selectedRow].slice(4).map((column, index) => ({
            title: features[index + 4],
            size: Math.abs(column),
            color: column < 0 ? "#bf212f" : "#006f3c",
          }));*/
    const a = {
      children: [
        {
          title: "neg",
          children: positiveChildren,
          color: "#bf212f",
        },
        {
          title: "pos",
          children: negativeChildren,
          color: "#006f3c",
        },
      ],
    };
    console.log(a);
    return a;
  }, [selectedRow]);

  const [treeMapDimensions, setTreeMapDimensions] = useState({
    width: 0,
    height: 0,
  });

  const treeMapContainer = useCallback((treeMapContainerElement) => {
    if (treeMapContainerElement !== null) {
      const {
        clientWidth: width,
        clientHeight: height,
      } = treeMapContainerElement;
      setTreeMapDimensions({ width, height });
    }
  }, []);

  return (
    <main>
      <section id="mosaic">
        {features.map((feature, index) => (
          <div key={`chart-${index}`}>
            {feature} {index}
          </div>
        ))}
      </section>
      <section id="main" ref={treeMapContainer}>
        <Treemap
          title={"Test"}
          data={treeChartData}
          mode="binary"
          hideRootNode
          colorType="literal"
          onLeafMouseOver={console.log}
          onLeafMouseOut={console.log}
          onLeafClick={console.log}
          {...treeMapDimensions}
        >
          {/*<Hint value={13}>
            <div style={{ background: "black" }}>
              <h3>Value of hint</h3>
        </div>
          </Hint>*/}
        </Treemap>
      </section>
      <section>
        <table>
          <thead>
            <tr>
              {tableColumns.map((column, index) => (
                <th key={`feature-${index}`}>{column}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {tableRows.map((row, index) => (
              <tr
                key={`row-${index}`}
                className={index === selectedRow ? "selected" : ""}
                onClick={() => setSelectedRow(index)}
              >
                {tableColumns.map((column, index) => (
                  <td key={`feature-${index}`}>{row[column]}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </main>
  );
}

export default App;
