import React, { useState } from "react";

import "react-vis/dist/style.css";

import "./App.css";
import { GlobalExplanations, TreemapContainer } from "./components";

const features = [
  "LotArea",
  "YearBuilt",
  "GrLivArea",
  "KitchenAbvGr",
  "FirstFlrSF",
  "PoolArea",
  "TotalBsmtSF",
  "BsmtFinSFOne",
  "BsmtUnfSF",
  "GarageYrBlt",
  "EnclosedPorch",
  "WoodDeckSF",
  "MSSubClass",
  "MiscVal",
  "OpenPorchSF",
  "ThreeSsnPorch",
  "YearRemodAdd",
  "YrSold",
  "GarageArea",
  "LowQualFinSF",
  "BsmtFinSFTwo",
  "OverallCond",
  "MoSold",
  "SecondFlrSF",
  "TotRmsAbvGrd",
  "ScreenPorch",
  "Fireplaces",
  "OverallQual",
  "HalfBath",
  "MasVnrArea",
  "GarageCars",
  "LotFrontage",
  "BsmtFullBath",
  "FullBath",
  "BedroomAbvGr",
  "BsmtHalfBath",
];

const tableColumns = ["ID", "Actual", "Predicted", "Difference", ...features];

const tableRows = new Array(tableColumns.length).fill(1).map(() => {
  const row = {};
  tableColumns.forEach(
    (tableColumn) => (row[tableColumn] = Math.random() * 1000 - 500)
  );
  return row;
});

function App() {
  const [selectedRowIndex, setSelectedRowIndex] = useState();

  return (
    <main>
      <GlobalExplanations
        features={features}
        selectedRowIndex={selectedRowIndex}
        tableRows={tableRows}
      />
      <TreemapContainer
        features={features}
        selectedRowIndex={selectedRowIndex}
        tableRows={tableRows}
      />
      <section>
        <table>
          <thead>
            <tr>
              {tableColumns.map((column, index) => (
                <th key={`feature-${index}`}>{column}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {tableRows.map((row, index) => (
              <tr
                key={`row-${index}`}
                className={index === selectedRowIndex ? "selected" : ""}
                onClick={() => setSelectedRowIndex(index)}
              >
                {tableColumns.map((column, index) => (
                  <td key={`feature-${index}`}>{row[column]}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </main>
  );
}

export default App;
