import { useState } from "react";
import {
  LineSeries,
  MarkSeries,
  Treemap,
  VerticalBarSeries,
  XYPlot,
} from "react-vis";

import "./App.css";

const cols = new Array(10).fill(1);
const rows = new Array(50)
  .fill(1)
  .map(() => cols.map(() => "a".repeat(Math.floor(Math.random() * 50))));

const data = {
  children: [
    { title: "a", color: "red", size: 1 },
    { title: "b", color: "blue", size: 1 },
    { title: "c", color: "green", size: 1 },
    { title: "d", color: "yellow", size: 1 },
  ],
};

const datae = [
  { x: 0, y: 8 },
  { x: 1, y: 5 },
  { x: 2, y: 4 },
  { x: 3, y: 9 },
  { x: 4, y: 1 },
  { x: 5, y: 7 },
  { x: 6, y: 6 },
  { x: 7, y: 3 },
  { x: 8, y: 2 },
  { x: 9, y: 0 },
];

function App() {
  const [selectedRow, setSelectedRow] = useState();

  return (
    <main>
      <section id="mosaic">
        {cols.map((col, index) => (
          <div key={`charta${index}`}>a</div>
        ))}
      </section>
      <section id="main">
        Selected row: {selectedRow}
        <Treemap
          title={"Test"}
          width={500}
          height={500}
          data={data}
          mode="binary"
          hideRootNode
          renderMode="SVG"
          colorType="literal"
        />
      </section>
      <section>
        <table>
          <thead>
            <tr>
              {cols.map((col, index) => (
                <th key={`feat${index}`}>Feature {index}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {rows.map((row, index) => (
              <tr
                key={`row${index}`}
                className={index === selectedRow ? "selected" : ""}
                onClick={() => setSelectedRow(index)}
              >
                {row.map((col, index) => (
                  <td key={`cell${index}`}>{col}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </main>
  );
}

export default App;
