import React, { useMemo, useState } from "react";
import { Hint, Treemap } from "react-vis";

import "react-vis/dist/style.css";

import "./App.css";

const data = {
  children: [
    { title: "a", size: 1 },
    { title: "b", size: 13 },
    { title: "c", size: 15 },
    { title: "d", size: 156 },
  ],
};

const tableColumns = [
  "ID",
  "Actual",
  "Predicted",
  "Difference",
  "Intercept",
  "LotArea",
  "YearBuilt",
  "GrLivArea",
  "KitchenAbvGr",
  "FirstFlrSF",
  "PoolArea",
  "TotalBsmtSF",
  "BsmtFinSFOne",
  "BsmtUnfSF",
  "GarageYrBlt",
  "EnclosedPorch",
  "WoodDeckSF",
  "MSSubClass",
  "MiscVal",
  "OpenPorchSF",
  "ThreeSsnPorch",
  "YearRemodAdd",
  "YrSold",
  "GarageArea",
  "LowQualFinSF",
  "BsmtFinSFTwo",
  "OverallCond",
  "MoSold",
  "SecondFlrSF",
  "TotRmsAbvGrd",
  "ScreenPorch",
  "Fireplaces",
  "OverallQual",
  "HalfBath",
  "MasVnrArea",
  "GarageCars",
  "LotFrontage",
  "BsmtFullBath",
  "FullBath",
  "BedroomAbvGr",
  "BsmtHalfBath",
];

const tableRows = new Array(tableColumns.length)
  .fill(1)
  .map(() => new Array(tableColumns.length).fill(1).map(() => Math.random()));
function App() {
  const [selectedRow, setSelectedRow] = useState();

  const treeChartData = useMemo(
    () => ({
      children:
        selectedRow === undefined
          ? []
          : tableRows[selectedRow].map((column, index) => ({
              title: tableColumns[index],
              size: column,
            })),
    }),
    [selectedRow]
  );

  return (
    <main>
      <section id="mosaic">
        {tableColumns.map((col, index) => (
          <div key={`chart-${index}`}>{col}</div>
        ))}
      </section>
      <section id="main">
        Selected row: {selectedRow}
        <Treemap
          title={"Test"}
          width={500}
          height={500}
          data={treeChartData}
          mode="binary"
          hideRootNode
          colorType="literal"
          margin={10}
          padding={10}
          onLeafMouseOver={console.log}
          onLeafMouseOut={console.log}
          onLeafClick={console.log}
        >
          <Hint value={13}>
            <div style={{ background: "black" }}>
              <h3>Value of hint</h3>
            </div>
          </Hint>
        </Treemap>
      </section>
      <section>
        <table>
          <thead>
            <tr>
              {tableColumns.map((column, index) => (
                <th key={`feature-${index}`}>{column}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {tableRows.map((row, index) => (
              <tr
                key={`row-${index}`}
                className={index === selectedRow ? "selected" : ""}
                onClick={() => setSelectedRow(index)}
              >
                {row.map((col, index) => (
                  <td key={`cell-${index}`}>{col}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </main>
  );
}

export default App;
