import "./App.css";

const cols = new Array(10).fill(1);
const rows = new Array(50).fill(1);

function App() {
  const [selectedRow, setSelectedRow] = useState();

  return (
    <main>
      <section id="mosaic">
        <div></div>
        <div></div>
        <div></div>
        <div></div>
        <div></div>
        <div></div>
        <div></div>
        <div></div>
        <div></div>
        <div></div>
      </section>
      <section></section>
      <section>
        <table>
          <thead>
            {cols.map((col, index) => (
              <th>Feature {index}</th>
            ))}
          </thead>
          <tbody>
            {rows.map((row, index) => (
              <tr
                className={index === selectedRow ? "selected" : ""}
                onClick={() => setSelectedRow(index)}
              >
                {cols.map(() => (
                  <td>
                    {rows
                      .slice(0, Math.floor(Math.random() * rows.length + 1))
                      .map(() => "a")}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </main>
  );
}

export default App;
