import React, { Component } from "react";
import {
  FlexibleWidthXYPlot,
  HorizontalGridLines,
  LineSeries,
  MarkSeries,
  XAxis,
  YAxis,
} from "react-vis";

/*const mark = useMemo(() => {
    return selectedRowIndex
      ? 
      : {};
  }, [selectedRowIndex]); */

export class GlobalExplanation extends Component {
  constructor(props) {
    super(props);

    const { feature, tableRows } = props;
    this.state = {
      selectedDataPoint: [],
      data: tableRows.reduce((previousRows, currentRow) => {
        const currentX = currentRow[feature];

        const firstBiggerElementIndex = previousRows.findIndex(
          (previousRow) => previousRow.x > currentX
        );

        const insertionIndex =
          firstBiggerElementIndex < 0
            ? previousRows.length
            : firstBiggerElementIndex;

        return [
          ...previousRows.slice(0, insertionIndex),
          {
            x: currentX,
            y: currentX * Math.random(),
          },
          ...previousRows.slice(insertionIndex),
        ];
      }, []),
    };
  }

  render() {
    const { feature } = this.props;
    const { data, selectedDataPoint } = this.state;
    return (
      <div>
        {feature}

        <FlexibleWidthXYPlot height={200}>
          <HorizontalGridLines />
          <LineSeries color="blue" data={data} />
          <MarkSeries color="orange" data={selectedDataPoint} />
          <XAxis />
          <YAxis />
        </FlexibleWidthXYPlot>
      </div>
    );
  }

  componentDidUpdate(prevProps) {
    const { feature, selectedRowIndex, tableRows } = this.props;
    if (prevProps.selectedRowIndex !== selectedRowIndex) {
      console.log("new row");
      this.setState({
        selectedDataPoint: [
          data.find(
            (dataPoint) => dataPoint.x === tableRows[selectedRowIndex][feature]
          ),
        ],
      });
    }
  }
}
