import React, { useMemo, useState } from "react";
import {
  FlexibleWidthXYPlot,
  HorizontalGridLines,
  LineSeries,
  MarkSeries,
  XAxis,
  YAxis,
} from "react-vis";

import "react-vis/dist/style.css";

import "./App.css";
import { TreemapContainer } from "./components";

const features = [
  "LotArea",
  "YearBuilt",
  "GrLivArea",
  "KitchenAbvGr",
  "FirstFlrSF",
  "PoolArea",
  "TotalBsmtSF",
  "BsmtFinSFOne",
  "BsmtUnfSF",
  "GarageYrBlt",
  "EnclosedPorch",
  "WoodDeckSF",
  "MSSubClass",
  "MiscVal",
  "OpenPorchSF",
  "ThreeSsnPorch",
  "YearRemodAdd",
  "YrSold",
  "GarageArea",
  "LowQualFinSF",
  "BsmtFinSFTwo",
  "OverallCond",
  "MoSold",
  "SecondFlrSF",
  "TotRmsAbvGrd",
  "ScreenPorch",
  "Fireplaces",
  "OverallQual",
  "HalfBath",
  "MasVnrArea",
  "GarageCars",
  "LotFrontage",
  "BsmtFullBath",
  "FullBath",
  "BedroomAbvGr",
  "BsmtHalfBath",
];

const tableColumns = ["ID", "Actual", "Predicted", "Difference", ...features];

const tableRows = new Array(tableColumns.length).fill(1).map(() => {
  const row = {};
  tableColumns.forEach(
    (tableColumn) => (row[tableColumn] = Math.random() * 1000 - 500)
  );
  return row;
});

const lineSeriesData = features.reduce(
  (lineSeriesData, feature) => ({
    ...lineSeriesData,
    [feature]: tableRows.reduce((previousRows, currentRow) => {
      const currentX = currentRow[feature];

      const firstBiggerElementIndex = previousRows.findIndex(
        (previousRow) => previousRow.x > currentX
      );

      const insertionIndex =
        firstBiggerElementIndex < 0
          ? previousRows.length
          : firstBiggerElementIndex;

      return [
        ...previousRows.slice(0, insertionIndex),
        {
          x: currentX,
          y: currentX * Math.random(),
        },
        ...previousRows.slice(insertionIndex),
      ];
    }, []),
  }),
  {}
);

function App() {
  const [selectedRowIndex, setSelectedRowIndex] = useState();

  const mark = useMemo(() => {
    return selectedRowIndex
      ? features.reduce(
          (markData, feature) => ({
            ...markData,
            [feature]: [
              lineSeriesData[feature].find(
                (d) => d.x === tableRows[selectedRowIndex][feature]
              ),
            ],
          }),
          {}
        )
      : {};
  }, [selectedRowIndex]);

  return (
    <main>
      <section id="mosaic">
        {features.map((feature, index) => (
          <div key={`chart-${index}`}>
            {feature}

            <FlexibleWidthXYPlot height={200}>
              <HorizontalGridLines />
              <LineSeries color="blue" data={lineSeriesData[feature]} />
              <LineSeries
                color="red"
                data={
                  mark[feature]
                    ? [...mark[feature], { x: mark[feature].x, y: 0 }]
                    : undefined
                }
              />
              <MarkSeries color="orange" data={mark[feature]} />
              <XAxis />
              <YAxis />
            </FlexibleWidthXYPlot>
          </div>
        ))}
      </section>
      <TreemapContainer
        features={features}
        selectedRowIndex={selectedRowIndex}
        tableRows={tableRows}
      />
      <section>
        <table>
          <thead>
            <tr>
              {tableColumns.map((column, index) => (
                <th key={`feature-${index}`}>{column}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {tableRows.map((row, index) => (
              <tr
                key={`row-${index}`}
                className={index === selectedRowIndex ? "selected" : ""}
                onClick={() => setSelectedRowIndex(index)}
              >
                {tableColumns.map((column, index) => (
                  <td key={`feature-${index}`}>{row[column]}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </main>
  );
}

export default App;
